import {
  PRODUCT_LIST_REQUEST,
  PRODUCT_LIST_SUCCESS,
  PRODUCT_LIST_FAIL,
} from "../constants/productConstants";
import Axios from "axios";

const listProducts = () => async (callServer) => {
  try {
    callServer({ type: PRODUCT_LIST_REQUEST });
    const { data } = await Axios.get("/api/products");
    callServer({ type: PRODUCT_LIST_SUCCESS, payload: data });
  } catch (error) {
    callServer({ type: PRODUCT_LIST_FAIL, payload: error.message });
  }
};

export { listProducts };
